elementClick.customFunction:
let $injector = widgetContext.$scope.$injector;
let customDialog = $injector.get(widgetContext.servicesMap
    .get('customDialog'));
let assetService = $injector.get(widgetContext.servicesMap
    .get('assetService'));
let customerService = $injector.get(widgetContext
    .servicesMap
    .get('customerService'));
let deviceService = $injector.get(widgetContext.servicesMap
    .get('deviceService'));
let attributeService = $injector.get(widgetContext
    .servicesMap.get('attributeService'));
let entityRelationService = $injector.get(widgetContext
    .servicesMap.get('entityRelationService'));
let entityGroupService = $injector.get(widgetContext
    .servicesMap.get('entityGroupService'));
let userService = $injector.get(widgetContext
    .servicesMap.get('userService'));



openAddEntityDialog();

function openAddEntityDialog() {
    customDialog.customDialog(htmlTemplate,
        AddEntityDialogController).subscribe();
}

function AddEntityDialogController(instance) {
    let vm = instance;
    vm.users = [];
    var sharedDashboardPermission = {
        "userGroupId": {
            "entityType": "ENTITY_GROUP",
            "id": undefined
        },
        "entityGroupOwnerId": {
            "entityType": "TENANT",
            "id": "9c28c0b0-b106-11ed-a5f7-cdf9aa24320a"
        },
        "role": {
            "type": "GROUP",
            "id": {
                "entityType": "ROLE",
                "id": "1137e970-b400-11ed-b907-3bebb3a56b7b"
            }
        },
        "entityGroupId": {
            "entityType": "ENTITY_GROUP",
            "id": "e03bef10-b3ff-11ed-b907-3bebb3a56b7b"
        },
        "userGroupOwnerId": null,
        "roleId": {
            "entityType": "ROLE",
            "id": "1137e970-b400-11ed-b907-3bebb3a56b7b"
        }
    }


    vm.addEntityFormGroup = vm.fb.group({
        email: ['', [vm.validators.email]],
        title: ['', [vm.validators.required]],
        attributes: vm.fb.group({
            lat: [null, [vm.validators
                .required
            ]],
            long: [null, [vm.validators
                .required
            ]]
        })
    });
    vm.cancel = function() {
        vm.dialogRef.close(null);
    };


    $(document).ready(function() {
        vm.mymap = L.map("mapContainer")
            .setView([19.115554992457728,
                    73.02786222338501
                ],
                13);
        L.tileLayer(
            'http://{s}.tile.osm.org/{z}/{x}/{y}.png', {
                // attribution: '&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
            }).addTo(vm.mymap);

        if (window.GeoSearch) {
            const provider = new window.GeoSearch
                .OpenStreetMapProvider();
            const search = new GeoSearch
                .GeoSearchControl({
                    provider: provider,
                    style: 'bar',
                    updateMap: true,
                    autoClose: true,
                }); 
            vm.mymap.addControl(search);
        }


        vm.mymap.on('click', function(e) {

            var lat = (e.latlng.lat);
            var lng = (e.latlng.lng);
            if (vm.marker == null) {
                vm.marker = L.marker([lat,
                    lng
                ], {
                    icon: vm
                        .markerIcon
                }).addTo(vm.mymap);

            } else {
                var newLatLng = new L
                    .LatLng(lat, lng);
                vm.marker.setLatLng(
                    newLatLng);

            }
            vm.addEntityFormGroup.controls
                .attributes.controls.lat
                .patchValue(lat, {
                    emitEvent: true
                });
            vm.addEntityFormGroup.controls
                .attributes.controls.long
                .patchValue(lng, {
                    emitEvent: true
                });


        });
    });


    vm.save = function() {

        vm.addEntityFormGroup.markAsPristine();

        const formValues = vm.addEntityFormGroup.value;
        let customer = {
            "title": formValues.title,
            "name": formValues.title,
            "email": formValues.email,
        };


        widgetContext.rxjs.forkJoin([customerService
            .saveCustomer(customer)
        ]).subscribe(function(cusResult) {
            let custUser = {
                "title": formValues.title,
                "name": formValues.title,
                "additionalInfo": {
                    "description": "",
                    "defaultDashboardId": "d63010a0-b3ff-11ed-b907-3bebb3a56b7b",
                    "defaultDashboardFullscreen": true
                },
                "email": formValues.email,
                "authority": "CUSTOMER_USER",
                "customerId": cusResult[0]
                    .id,
                "ownerId": cusResult[0].id
            }; //	Self Registration Users
            widgetContext.rxjs.forkJoin([

                // widgetContext.entityGroupService.saveEntityGroup({"name":"Customer Users","additionalInfo":{"description":""},"type":"USER","ownerId":cusResult[0].id}),
                widgetContext.http.get(
                    "/api/entityGroups/CUSTOMER/" +
                    cusResult[0].id.id +
                    "/USER"),
                // entityGroupService.addEntityToEntityGroup("b17534b0-331d-11ed-aea7-a3a5a9b95115",cusResult[0].id.id),
                userService.saveUser(
                    custUser, false),
                saveAttributes(
                    cusResult[0].id),
                saveRelation({
            "entityType": "TENANT",
            "id": widgetContext.currentUser.tenantId
            },cusResult[0].id)
        ]).subscribe(function(result) {

                var userGroup = result[
                    0].find(e => e
                    .name ==
                    "Customer Users"
                    )
                sharedDashboardPermission["userGroupId"]["id"]=userGroup.id.id
                rxjs.forkJoin(
                        entityGroupService
                        .addEntityToEntityGroup(
                            userGroup.id
                            .id, result[
                                1].id.id
                            ),
                        widgetContext
                        .http.post(
                            "/api/groupPermission",
                            sharedDashboardPermission
                            ))
                    .subscribe(function(
                        userSaveResponse
                        ) {
                        widgetContext
                            .showToast(
                                'success',
                                'Customer Added',
                                1000,
                                'top',
                                'left','root'
                                );
                        widgetContext
                            .updateAliases();
                        vm.dialogRef
                            .close(
                                null
                                );
                    })
            })


        },function(err){
            console.log("save customer err: ",err);

        })



    };
    function saveRelation(fromEntityId,toEntityId){
        return entityRelationService.saveRelation({
                    "from":  fromEntityId,
                    "to": toEntityId,
                    "type": "Contains",
                    "typeGroup": "COMMON",
                    "additionalInfo": {}
                })
    }
    function saveAttributes(entityId) {
        let attributes = vm.addEntityFormGroup.get(
            'attributes').value;
        let attributesArray = [];
        for (let key in attributes) {
            if (attributes[key] !== null) {
                attributesArray.push({
                    key: key,
                    value: attributes[key]
                });
            }
        }
        if (attributesArray.length > 0) {
            return attributeService.saveEntityAttributes(
                entityId, "SERVER_SCOPE",
                attributesArray);
        }
        return widgetContext.rxjs.of([]);
    }
    vm.markerIcon = L.icon({
        iconSize: [25, 25], // size of the icon
        iconAnchor: [12.5, 25],
        iconUrl: "data:image/jpeg;base64,",
        popupAnchor: [0, -
            30
        ] // point from which the popup should open relative to the iconAnchor

    });
    vm.closeIcon =
        "data:image/png;base64,"
}

