    if(result.data.length>=1){
        widgetContext.http.get("/api/user/"+result.data[0].id.id+"/token").subscribe(function(token){
            storeToken("jwt_token",token["token"])
            storeToken("refresh_token",token["refreshToken"])
            window.location = '/';   

        })

    }
},function(error){
    console.log("Error: ",error)
},function(complete){
    console.log("Request completed")
})

function storeToken(prefix,token){
    var tokenData=parseJwt(token)
    const issuedAt = tokenData["iat"];
    const expTime = tokenData["exp"];
    if (issuedAt && expTime) {
        const ttl = expTime - issuedAt;
        if (ttl > 0) {
            console.log("storing token: ",prefix)
            const clientExpiration = new Date().valueOf() + ttl * 1000;
            localStorage.setItem(prefix, token);
            localStorage.setItem(prefix+'_expiration', '' + clientExpiration);
        }
    }

}
function parseJwt (token) {
    var base64Url = token.split('.')[1];
    var base64 = base64Url.replace(/-/g, '+').replace(/_/g, '/');
    var jsonPayload = decodeURIComponent(window.atob(base64).split('').map(function(c) {
        return '%' + ('00' + c.charCodeAt(0).toString(16)).slice(-2);
    }).join(''));

    return JSON.parse(jsonPayload);
}

