    .servicesMap.get('userService'));
openAddEntityDialog();

function openAddEntityDialog() {
    customDialog.customDialog(htmlTemplate,
        AddEntityDialogController).subscribe();
}

function AddEntityDialogController(instance) {
    let vm = instance;
    vm.deviceTypes = [];
    vm.entity=undefined;
    vm.devices=[];
    vm.addEntityFormGroup = vm.fb.group({
        deviceType:['', [vm.validators.required]],
        device:['', [vm.validators.required]],
        attributes: vm.fb.group({
        inactivityTimeout:[0, [vm.validators.pattern(/^-?[0-9]+$/)]]
     })
    });
    vm.cancel = function() {
        vm.dialogRef.close(null);
    };
    vm.displayEntityFn = function(entity) {
        return entity ? entity.name : undefined;
    }
    vm.deviceTypeSelected=function(value){
        vm.devices=[];
        vm.addEntityFormGroup.controls["attributes"].patchValue({
                inactivityTimeout: value=="Oil Condition Sensor" ?60000:300000
                }, {
            emitEvent: true
            });

        loadDevices(widgetContext.pageLink(pageSize = 100,
        page = 0));

    }
    vm.onDeviceFocus = function() {
        if (vm.addEntityFormGroup.controls
            .device.value == null || vm.addEntityFormGroup.controls
            .device.value == "") {
            vm.addEntityFormGroup.patchValue({
                device: ""
            }, {
                emitEvent: true
            });
        }
    }

    function _filteredCustomerDevices(value) {
        if (value != null && value.id) {
            return vm.devices.filter(option => option
                .name && option.name.toLowerCase()
                .includes(value.name.toLowerCase()));
        }
        const filterValue = value.toLowerCase();
        return vm.devices.filter(option => option
            .name && option.name.toLowerCase().includes(
                filterValue));
    }
    vm.filteredCustomerDevices = vm.addEntityFormGroup.controls
        .device.valueChanges.pipe(window.rxjs.operators
            .map(
                value => _filteredCustomerDevices(value))
        );

    vm.save = function() {
        const formValues = vm.addEntityFormGroup.value;
        vm.addEntityFormGroup.markAsPristine();
        if(formValues.device.id==undefined){
                saveDeviceObservable().subscribe(function(savedDevice){
                widgetContext.rxjs.forkJoin([saveRelation(entityId,savedDevice.id),saveAttributes(savedDevice.id)]).subscribe(function(){
                        widgetContext.updateAliases();
                        vm.dialogRef.close(null);
                    });

                })
            }       
        else{
                widgetContext.entityRelationService.findByTo(formValues.device.id).subscribe(function(haveRelation){
                    console.log(haveRelation)
                    if(haveRelation.length>=1){
                        showErrorDialog("Can not add","Already assigned to another ship/engine");
                    }
                    else{
                widgetContext.rxjs.forkJoin([saveRelation(entityId,formValues.device.id),saveAttributes(entityId)]).subscribe(function(){
                        widgetContext.updateAliases();
                        vm.dialogRef.close(null);
                    });

                    }
                })


                }

    };
        function saveAttributes(entityId) {
        let attributes = vm.addEntityFormGroup.get('attributes').value;
        let attributesArray = [];
        for (let key in attributes) {
            if(attributes[key] !== null) {
                attributesArray.push({key: key, value: attributes[key]});
            }
        }
        if (attributesArray.length > 0) {
            return attributeService.saveEntityAttributes(entityId, "SERVER_SCOPE", attributesArray);
        }
        return widgetContext.rxjs.of([]);
    }

    function showErrorDialog(title,content) {
        dialogs.alert(title, content, 'CLOSE').subscribe(
            function(result) {}
        );
    }
    function saveRelation(fromEntityId,toEntityId){
        return entityRelationService.saveRelation({
                    "from":  fromEntityId,
                    "to": toEntityId,
                    "type": "Contains",
                    "typeGroup": "COMMON",
                    "additionalInfo": {}
                })
    }

    function saveDeviceObservable() {
        const formValues = vm.addEntityFormGroup.value;
        let entity = {
            name: formValues.device,
            type: formValues.deviceType,
            "customerId": vm.entity.customerId,
            "ownerId": vm.entity.customerId,

        };
        return widgetContext.deviceService.saveDevice(entity);
    }



    function loadDevices(pageLink) {
        deviceService.getUserDevices(pageLink,vm.addEntityFormGroup.value.deviceType)
            .subscribe(function(result) {
                    vm.devices = vm.devices.concat(result.data)
                    if (result.hasNext ==true) {
                        loadDevices(
                            widgetContext.pageLink(pageSize =100,page =pageLink.page +1
                            ));
                    } else {
                        vm.addEntityFormGroup.patchValue({
                            devices: ""
                        }, {
                            emitEvent: true
                        });

                    }

                },
                function(err) {
                    console.log("Error: ", err);
                })

    }
        function loadEngineData(){
        widgetContext.rxjs.forkJoin(widgetContext.entityService.getEntity(entityId.entityType,entityId.id),entityRelationService.findByTo(entityId)).subscribe(function(res){
            vm.entity=res[0];
            if(vm.entity.type=="Engine"){
                vm.deviceTypes.push("vBox-Core Engine")
            }
            else if(vm.entity.type=="Motor"){
                vm.deviceTypes.push("vBox-Core Motor")
            }
            else if(vm.entity.type=="Gearbox"){
                vm.deviceTypes.push("vBox-Core Gearbox")
            }
            else if(vm.entity.type=="Torque"){
                vm.deviceTypes.push("vBox-Core Torque")
            }            
            else if(vm.entity.type=="Turbine"){
                vm.deviceTypes.push("vBox-Core Turbine")
            }
            vm.deviceTypes.push("Oil Condition Sensor")
            console.log("vm.entity: ",vm.entity)
        })
    }
    loadEngineData();
    vm.closeIcon =
        "data:image/png;base64,"
}

