    .servicesMap.get('entityGroupService'));
let userService = $injector.get(widgetContext
    .servicesMap.get('userService'));

openAddEntityDialog();

function openAddEntityDialog() {
    customDialog.customDialog(htmlTemplate,
        AddEntityDialogController).subscribe();
}

function AddEntityDialogController(instance) {
    console.log(instance)
    let vm = instance;
    vm.customers = [];

    vm.addEntityFormGroup = vm.fb.group({
        customer: ['', [vm.validators.required]],
        shipName: ['', [vm.validators.required]],
        shipLabel: [''],
        attributes: vm.fb.group({
         floorMap:[null, [vm.validators.required]]
        })
    });


    vm.onFloorMapSelect = function(event) {
        var output = document.getElementById(
            'imageLoader');


        const file = event.target.files[0];
        const reader = new FileReader();

        reader.addEventListener("load", function() {
            vm.selectedFloorMap = reader.result;
            output.src = reader.result;
$(output).css('background-image', 'url(' + reader.result + ')');            

vm.addEntityFormGroup.controls.attributes.controls.floorMap.patchValue(reader.result, {
                            emitEvent: true
                        });

        }, false);

        if (file) {
            reader.readAsDataURL(file);
        }

    };

    vm.cancel = function() {
        vm.dialogRef.close(null);
    };
    vm.displayEntityFn = function(entity) {
        return entity ? entity.name : undefined;
    }

    vm.onCustomerFocus = function() {
        if (vm.addEntityFormGroup.controls
            .customer.value == null) {
            vm.addEntityFormGroup.patchValue({
                customer: ""
            }, {
                emitEvent: true
            });

        }
    }


    function _filteredCustomers(value) {
        if (value != null && value.id) {
            return vm.customers.filter(option => option
                .name && option.name.toLowerCase()
                .includes(value.name.toLowerCase()));
        }
        const filterValue = value.toLowerCase();
        return vm.customers.filter(option => option
            .name && option.name.toLowerCase().includes(
                filterValue));
    }

    vm.onfileSelectClicked=function(event){
        console.log("onfileSelectClicked",event.target)
        var inputElement=$(event.target).find('.npFileUploader')
        if(inputElement.length==0){
            inputElement=$(event.target).siblings('.npFileUploader')
        }
        if(inputElement.length!=0){
            console.log(inputElement)
            $(inputElement[0]).trigger('click');
        }
    }

    vm.filteredCustomers = vm.addEntityFormGroup.controls
        .customer.valueChanges.pipe(window.rxjs.operators
            .map(
                value => _filteredCustomers(value))
        );

    loadCustomers(widgetContext.pageLink(pageSize = 100,
        page = 0));


    vm.save = function() {
        const formValues = vm.addEntityFormGroup.value;
        vm.addEntityFormGroup.markAsPristine();
        saveEntityObservable().subscribe(function(entity){
            widgetContext.rxjs.forkJoin(entityRelationService.saveRelation({
                "from":  formValues.customer.id,
                "to": entity.id,
                "type": "Contains",
                "typeGroup": "COMMON",
                "additionalInfo": {}
            }),saveAttributes(entity.id)).subscribe(function(){
                widgetContext.updateAliases();
                vm.dialogRef.close(null);
            });
        });
    };

    function saveAttributes(entityId) {
        let attributes = vm.addEntityFormGroup.get('attributes').value;
        let attributesArray = [];
        for (let key in attributes) {
            if(attributes[key] !== null) {
                attributesArray.push({key: key, value: attributes[key]});
            }
        }
        if (attributesArray.length > 0) {
            return attributeService.saveEntityAttributes(entityId, "SERVER_SCOPE", attributesArray);
        }
        return widgetContext.rxjs.of([]);
    }
    function saveEntityObservable() {
        const formValues = vm.addEntityFormGroup.value;
        let entity = {
            name:formValues.customer.id.id+"_"+formValues.shipName,
            label:formValues.shipLabel,
            type: "ship",
            "customerId": formValues.customer.id,
            "ownerId": formValues.customer.id
        };
        return assetService.saveAsset(entity);
    }

    function loadCustomers(pageLink) {
        customerService.getCustomers(pageLink)
            .subscribe(function(result) {
                    vm.customers = vm.customers.concat(result.data)
                    if (result.hasNext ==true) {
                        loadCustomers(
                            widgetContext.pageLink(pageSize =100,page =pageLink.page +1
                            ));
                    } else {
                        vm.addEntityFormGroup.patchValue({
                            customer: ""
                        }, {
                            emitEvent: true
                        });

                    }

                },
                function(err) {
                    console.log("Error: ", err);
                })

    }
    vm.closeIcon =
        "data:image/png;base64,"
    vm.uploadIcon =
    "data:image/png;base64,"
}

