openAddEntityDialog();

function openAddEntityDialog() {
    customDialog.customDialog(htmlTemplate,
        AddEntityDialogController).subscribe();
}

function AddEntityDialogController(instance) {
    let vm = instance;
    vm.entity=undefined;
    vm.addEntityFormGroup = vm.fb.group({
        deviceName: [entityName, [vm.validators.required]],
        deviceLabel: [null],
    });
    vm.cancel = function() {
        vm.dialogRef.close(null);
    };
    vm.save = function() {
        const formValues = vm.addEntityFormGroup.value;
        vm.addEntityFormGroup.markAsPristine();
            widgetContext.rxjs.forkJoin([saveEngineObservable()])
            .subscribe(function(savedEntity){
                    widgetContext.updateAliases();
                    vm.dialogRef.close(null);
            });

    };
    function saveEngineObservable() {
        const formValues = vm.addEntityFormGroup.value;
        vm.entity["name"]=getCustomerId(vm.entity.name)+formValues.deviceName;
        vm.entity["label"]=formValues.deviceLabel;
        return deviceService.saveDevice(vm.entity);
    }
    function getCustomerId(value){
        const regexExp = /^[0-9a-fA-F]{8}\b-[0-9a-fA-F]{4}\b-[0-9a-fA-F]{4}\b-[0-9a-fA-F]{4}\b-[0-9a-fA-F]{12}$/gi;
        var underScoreSperated=value.split("_")
        if(underScoreSperated.length>1){
            if(regexExp.test(underScoreSperated[0])==true){
                return underScoreSperated[0]+"_"
            }
        }
        return value

    }

    function npNameToNormal(value){
        const regexExp = /^[0-9a-fA-F]{8}\b-[0-9a-fA-F]{4}\b-[0-9a-fA-F]{4}\b-[0-9a-fA-F]{4}\b-[0-9a-fA-F]{12}$/gi;
        var underScoreSperated=value.split("_")
        if(underScoreSperated.length>1){
            if(regexExp.test(underScoreSperated[0])==true){
                return value.replace(underScoreSperated[0]+"_","")
            }
        }
        return value

    }
    function loadData(){
        widgetContext.rxjs.forkJoin([
            widgetContext.entityService.getEntity(entityId.entityType,entityId.id)]).subscribe(function(res){
            vm.entity=res[0];
            vm.addEntityFormGroup.patchValue({
                    deviceName:npNameToNormal(vm.entity.name),
                    deviceLabel:vm.entity.label,
                }, {
                    emitEvent: true
                });
        })
    }

    vm.closeIcon =
        "data:image/png;base64,"

    loadData();


}

