    .servicesMap.get('userService'));
openAddEntityDialog();

function openAddEntityDialog() {
    customDialog.customDialog(htmlTemplate,
        AddEntityDialogController).subscribe();
}

function AddEntityDialogController(instance) {
    let vm = instance;
    vm.zoomImage={};
    vm.placedItemType = ['Engine','Motor','Gearbox','Torque','Turbine', 'Device','Zet Peller','Clutch'];
    vm.deviceTypes = ['NPGateway'];
    vm.entity=undefined;
    vm.devices=[];
    vm.addEntityFormGroup = vm.fb.group({
        componentName: ['', [vm.validators.required]],
        componentLabel: [''],
        itemType:['Engine', [vm.validators.required]],
        deviceType:['', [vm.validators.required]],
        device:['', [vm.validators.required]],
        deviceLabel:[''],
        attributes: vm.fb.group({
        xPos:[null, [vm.validators.required]],
        yPos:[null, [vm.validators.required]],
        thresholdWarning:[3, [vm.validators.required]],
        thresholdCritical:[5, [vm.validators.required]],
        mechHealth:[25, [vm.validators.required]],
        operatingHealth:[25, [vm.validators.required]],
        thresholdFuelLoss:[4, [vm.validators.required]],
        inactivityTimeout:[60000, [vm.validators.required]]
     })
    });
    vm.cancel = function() {
        vm.dialogRef.close(null);
    };
    vm.displayEntityFn = function(entity) {
        return entity ? entity.name : undefined;
    }
        vm.displayNPEntityFn = function(entity) {
        return entity ? npNameToNormal(entity.name) : undefined;
    }
    function npNameToNormal(value){
        const regexExp = /^[0-9a-fA-F]{8}\b-[0-9a-fA-F]{4}\b-[0-9a-fA-F]{4}\b-[0-9a-fA-F]{4}\b-[0-9a-fA-F]{12}$/gi;
        var underScoreSperated=value.split("_")
        if(underScoreSperated.length>1){
            if(regexExp.test(underScoreSperated[0])==true){
                return value.replace(underScoreSperated[0]+"_","")
            }
        }
        return value

    }
    vm.deviceTypeSelected=function(value){
        // if(value=="NPGateway"){

        // }
        vm.devices=[];
        loadDevices(widgetContext.pageLink(pageSize = 100,
        page = 0));

    }
    vm.onDeviceFocus = function() {

        if (vm.addEntityFormGroup.controls
            .device.value == null || vm.addEntityFormGroup.controls
            .device.value == "") {

            vm.addEntityFormGroup.patchValue({
                device: ""
            }, {
                emitEvent: true
            });
        }
    }

    function _filteredCustomerDevices(value) {
        if (value != null && value.id) {
            return vm.devices.filter(option => option
                .name && option.name.toLowerCase()
                .includes(value.name.toLowerCase()));
        }
        const filterValue = value.toLowerCase();
        return vm.devices.filter(option => option
            .name && option.name.toLowerCase().includes(
                filterValue));
    }
    vm.filteredCustomerDevices = vm.addEntityFormGroup.controls
        .device.valueChanges.pipe(window.rxjs.operators
            .map(
                value => _filteredCustomerDevices(value))
        );

    vm.placedItemSelected=function(value){
        if(value!=="Device"){
            vm.addEntityFormGroup.controls['componentName'].enable();
            vm.addEntityFormGroup.controls['componentLabel'].enable();
            vm.addEntityFormGroup.controls['deviceType'].disable();
            vm.addEntityFormGroup.controls['device'].disable();
            vm.addEntityFormGroup.controls['deviceLabel'].disable();
            var controls=vm.addEntityFormGroup.controls['attributes'].controls

            for (var control in controls) {
                    controls[control].enable()
                if(control=="inactivityTimeout"){
                    controls[control].disable()
                }
                else{
                    controls[control].enable()
                }            }


        }
        else{
            vm.addEntityFormGroup.controls['componentName'].disable();
            vm.addEntityFormGroup.controls['componentLabel'].disable();
            vm.addEntityFormGroup.controls['deviceType'].enable();
            vm.addEntityFormGroup.controls['device'].enable();
            vm.addEntityFormGroup.controls['deviceLabel'].enable();

            var controls=vm.addEntityFormGroup.controls['attributes'].controls
            for (var control in controls) {
                if(control=="xPos" || control=="yPos" || control=="inactivityTimeout"){
                    controls[control].enable()
                }
                else{
                    controls[control].disable()
                }
            }
        }
    }


    vm.save = function() {
        const formValues = vm.addEntityFormGroup.value;
        vm.addEntityFormGroup.markAsPristine();
        if(formValues.itemType!=="Device"){
            saveEngineObservable().subscribe(function(savedEntity){
                widgetContext.rxjs.forkJoin([saveRelation(entityId,savedEntity.id),saveAttributes(savedEntity.id)]).subscribe(saveSuccess());

            });
        }
        else {
            if(formValues.device.id==undefined){
                saveDeviceObservable().subscribe(function(savedDevice){
                widgetContext.rxjs.forkJoin([saveRelation(entityId,savedDevice.id),saveAttributes(savedDevice.id)]).subscribe(saveSuccess());

                })
            }       
            else{
                widgetContext.entityRelationService.findByTo(formValues.device.id).subscribe(function(haveRelation){

                    if(haveRelation.length>=1){
                        showErrorDialog("Can not add","Already assigned to another ship/engine");
                    }
                    else{
                        var _device=formValues.device;
                        _device.customerId=vm.entity.customerId
                        widgetContext.rxjs.forkJoin([changeOwner(_device.id),widgetContext.deviceService.saveDevice(_device), saveRelation(entityId,formValues.device.id),saveAttributes(entityId)]).subscribe(saveSuccess());

                // widgetContext.rxjs.forkJoin([changeOwner(formValues.device.id.id),saveRelation(entityId,formValues.device.id),saveAttributes(formValues.device.id)]).subscribe(saveSuccess());

                    }
                })


                }
        }

    };

    function saveSuccess(){
        broadcastService.broadcast(widgetContext.settings.refreshChildPrefix+entityId.id, {
                referesh : 'true'
        });
        widgetContext.updateAliases();
        vm.dialogRef.close(null);
    }
    function changeOwner(_deviceId){
        return widgetContext.http.post("/api/owner/CUSTOMER/"+vm.entity.customerId.id+"/DEVICE/"+_deviceId.id)
    }
    function showErrorDialog(title,content) {
        dialogs.alert(title, content, 'CLOSE').subscribe(
            function(result) {}
        );
    }
    function saveRelation(fromEntityId,toEntityId){
        return entityRelationService.saveRelation({
                    "from":  fromEntityId,
                    "to": toEntityId,
                    "type": "Contains",
                    "typeGroup": "COMMON",
                    "additionalInfo": {}
                })
    }
    function saveEngineObservable() {
        const formValues = vm.addEntityFormGroup.value;
        let entity = {
            // name: formValues.componentName,
            name:vm.entity.customerId.id+"_"+formValues.componentName,
            label:formValues.componentLabel,
            type: formValues.itemType,
            "customerId": vm.entity.customerId,
            "ownerId": vm.entity.customerId
        };
        console.log("Saving component: ",entity,vm.entity)
        return assetService.saveAsset(entity);
    }

    function saveDeviceObservable() {
        const formValues = vm.addEntityFormGroup.value;
        var entity= {
            name:vm.entity.customerId.id+"_"+formValues.device,
                // name: formValues.device,
                type: formValues.deviceType,
                label:formValues.deviceLabel,
                "customerId": vm.entity.customerId,
                "ownerId": vm.entity.customerId,
                "additionalInfo":formValues.deviceType=="NPGateway"?{"gateway":true,"overwriteActivityTime":false,"description":""}:{}
            };

        return widgetContext.deviceService.saveDevice(entity);
    }



    function loadDevices(pageLink) {
        deviceService.getUserDevices(pageLink,vm.addEntityFormGroup.value.deviceType)
            .subscribe(function(result) {
                    vm.devices = vm.devices.concat(result.data)
                    if (result.hasNext ==true) {
                        loadDevices(
                            widgetContext.pageLink(pageSize =100,page =pageLink.page +1
                            ));
                    } else {
                        vm.addEntityFormGroup.patchValue({
                            devices: ""
                        }, {
                            emitEvent: true
                        });

                    }

                },
                function(err) {
                    console.log("Error: ", err);
                })

    }

    function saveAttributes(entityId) {
        let attributes = vm.addEntityFormGroup.get('attributes').controls;
        let attributesArray = [];
        for (let key in attributes) {
            console.log(attributes[key])
            if(attributes[key]["status"] == "VALID") {
                attributesArray.push({key: key, value: attributes[key]["value"]});
            }
        }
        if (attributesArray.length > 0) {
            return attributeService.saveEntityAttributes(entityId, "SERVER_SCOPE", attributesArray);
        }
        return widgetContext.rxjs.of([]);
    }

    function loadShipData(){
        widgetContext.rxjs.forkJoin(widgetContext.entityService.getEntity(entityId.entityType,entityId.id),attributeService.getEntityAttributes(entityId, 'SERVER_SCOPE',["floorMap"])).subscribe(function(res){

            vm.entity=res[0];
            vm.FloorPlanCanvas = document.getElementById("FloorPlanCanvas");
            init(res[1][0].value)

        })
    }

    vm.closeIcon =
        "data:image/png;base64,"

    vm.placedItemSelected(vm.addEntityFormGroup.value["itemType"])
    loadShipData();


    function mouseDownImage(evt) {

        if (evt.ctrlKey) {
        document.body.style.mozUserSelect =
                    document.body.style.webkitUserSelect = document
                    .body.style.userSelect = 'none';
                vm.zoomImage.lastX = evt.offsetX || (evt.pageX -
                    vm.zoomImage.can.offsetLeft);
                vm.zoomImage.lastY = evt.offsetY || (evt.pageY -
                    vm.zoomImage.can.offsetTop);
                dragStart = vm.zoomImage.ct.transformedPoint(
                    vm.zoomImage.lastX, vm.zoomImage.lastY);
                dragged = false;
            } else {
                var p1 = vm.zoomImage.ct.transformedPoint(0, 0);
                var canvasPoints = GetCoordinates(
                    evt);

                processClick([p1["x"] + (
                    canvasPoints[0] /
                    vm.zoomImage.currentScale), p1[
                    "y"] + (
                    canvasPoints[1] /
                    vm.zoomImage.currentScale)]);
            }
        }

    function mouseMoveImage(evt) {
            if (evt.ctrlKey) {
                vm.zoomImage.lastX = evt.offsetX || (evt.pageX -
                    vm.zoomImage.can.offsetLeft);
                vm.zoomImage.lastY = evt.offsetY || (evt.pageY -
                    vm.zoomImage.can.offsetTop);
                dragged = true;
                if (dragStart) {
                    var pt = vm.zoomImage.ct.transformedPoint(
                        vm.zoomImage.lastX, vm.zoomImage.lastY);
                    vm.zoomImage.ct.translate(pt.x - dragStart.x,
                        pt.y - dragStart.y);
                    redraw(vm.zoomImage.ct, vm.zoomImage.can, vm.zoomImage.image);
                }
            }
        }

    function mouseUpImage(evt) {
            console.log("mouseUp")
            if (evt.ctrlKey) {
                dragStart = null;
                if (!dragged) zoom(evt.shiftKey ? -
                    1 : 1);
            }
        }
    function handleScroll(evt) {
            var delta = evt.wheelDelta ? evt
                .wheelDelta / 40 : evt.detail ? -evt
                .detail : 0;
            if (delta) zoom(delta);
            return evt.preventDefault() && false;
        };
    var zoom = function(clicks) {
            var pt = vm.zoomImage.ct.transformedPoint(vm.zoomImage.lastX, vm.zoomImage.lastY);
            vm.zoomImage.ct.translate(pt.x, pt.y);
            var factor = Math.pow(vm.zoomImage.scaleFactor, clicks);
            vm.zoomImage.currentScale = vm.zoomImage.currentScale * factor;
            vm.zoomImage.ct.scale(factor, factor);
            vm.zoomImage.ct.translate(-pt.x, -pt.y);
            redraw(vm.zoomImage.ct, vm.zoomImage.can, vm.zoomImage.image);
        }

    function setupZoomAndPan(ct, can, image) {
        trackTransforms(ct);
        vm.zoomImage.ct=ct;
        vm.zoomImage.can=can;
        vm.zoomImage.image=image;
        redraw(ct, can, image);
        vm.zoomImage.lastX = can.width / 2;
        vm.zoomImage.lastY = can.height / 2;
        vm.zoomImage.dragStart;
        vm.zoomImage.dragged;
        vm.zoomImage.scaleFactor = 1.1;
        vm.zoomImage.currentScale = 1.0;
        vm.zoomImage.canvasLastX = vm.zoomImage.lastX;
        vm.zoomImage.canvasLastY = vm.zoomImage.lastY;

        setToPosition(ct, can, image, image.width / 2, image
            .height / 2);

        can.removeEventListener('mousedown', mouseDownImage)
        can.addEventListener('mousedown',mouseDownImage , false);

        can.removeEventListener('mousemove', mouseMoveImage)
        can.addEventListener('mousemove',mouseMoveImage , false);

        can.removeEventListener('mouseup', mouseUpImage)
        can.addEventListener('mouseup',mouseUpImage , false);




        can.removeEventListener('DOMMouseScroll', handleScroll)
        can.addEventListener('DOMMouseScroll', handleScroll,
            false);
        can.removeEventListener('mousewheel', handleScroll)
        can.addEventListener('mousewheel', handleScroll, {
            passive: false
        });
    }

    function init(imageDataURL) {
        var img = new Image();
        img.onload = function() {
            var width = 600;
            var height = 300;
            vm.FloorPlanCanvas.width = width;
            vm.FloorPlanCanvas.height = height;
            var FloorPlanCtx = vm.FloorPlanCanvas
                .getContext("2d");
            FloorPlanCtx.drawImage(img, 0, 0);
            vm.floorImage = img;
            setupZoomAndPan(FloorPlanCtx,
                vm.FloorPlanCanvas, img);
        }
        img.src = imageDataURL;
    }

    function redraw(ct, can, image, ) {
        var p1 = ct.transformedPoint(0, 0);
        var p2 = ct.transformedPoint(can.width, can.height);
        ct.clearRect(p1.x, p1.y, p2.x - p1.x, p2.y - p1.y);
        ct.save();
        ct.setTransform(1, 0, 0, 1, 0, 0);
        ct.clearRect(0, 0, can.width, can.height);
        ct.restore();
        ct.drawImage(image, 0, 0);
        drawItemsOnFloorPlan(vm.FloorPlanCanvas);
    }

    function setToPosition(ct, can, image, x, y) {
        var pt = ct.transformedPoint(vm.zoomImage.canvasLastX,
            vm.zoomImage.canvasLastY);
        ct.translate((pt.x - x), (pt.y - y));
        redraw(ct, can, image);
    }

    function trackTransforms(ctx) {
        var svg = document.createElementNS(
            "http://www.w3.org/2000/svg", 'svg');
        var xform = svg.createSVGMatrix();
        ctx.getTransform = function() {
            return xform;
        };

        var savedTransforms = [];
        var save = ctx.save;
        ctx.save = function() {
            savedTransforms.push(xform.translate(0, 0));
            return save.call(ctx);
        };

        var restore = ctx.restore;
        ctx.restore = function() {
            xform = savedTransforms.pop();
            return restore.call(ctx);
        };

        var scale = ctx.scale;
        ctx.scale = function(sx, sy) {
            xform = xform.scaleNonUniform(sx, sy);
            return scale.call(ctx, sx, sy);
        };

        var rotate = ctx.rotate;
        ctx.rotate = function(radians) {
            xform = xform.rotate(radians * 180 / Math
                .PI);
            return rotate.call(ctx, radians);
        };

        var translate = ctx.translate;
        ctx.translate = function(dx, dy) {
            xform = xform.translate(dx, dy);
            return translate.call(ctx, dx, dy);
        };

        var transform = ctx.transform;
        ctx.transform = function(a, b, c, d, e, f) {
            var m2 = svg.createSVGMatrix();
            m2.a = a;
            m2.b = b;
            m2.c = c;
            m2.d = d;
            m2.e = e;
            m2.f = f;
            xform = xform.multiply(m2);
            return transform.call(ctx, a, b, c, d, e,
                f);
        };

        var setTransform = ctx.setTransform;
        ctx.setTransform = function(a, b, c, d, e, f) {
            xform.a = a;
            xform.b = b;
            xform.c = c;
            xform.d = d;
            xform.e = e;
            xform.f = f;
            return setTransform.call(ctx, a, b, c, d, e,
                f);
        };

        var pt = svg.createSVGPoint();
        ctx.transformedPoint = function(x, y) {
            pt.x = x;
            pt.y = y;
            return pt.matrixTransform(xform.inverse());
        }
    }

    function processClick(coordinates) {
        var xCord = coordinates[0];
        var yCord = coordinates[1];
        vm.addEntityFormGroup.controls.attributes.patchValue({
                    xPos: xCord/vm.floorImage.width,
                    yPos:yCord/vm.floorImage.height,
                }, {
                    emitEvent: false
                });
        // floorPlan["co-ord"].push(data);
        drawItemsOnFloorPlan(vm.FloorPlanCanvas);

    }


    function drawItemsOnFloorPlan(can) {
        var ctx = can.getContext('2d');
        ctx.clearRect(0, 0, vm.floorImage.width, vm.floorImage
            .height);
        ctx.globalAlpha = 1.0;
        ctx.drawImage(vm.floorImage, 0, 0);
        if(vm.addEntityFormGroup.controls.attributes.value.xPos!==undefined && vm.addEntityFormGroup.controls.attributes.value.yPos!==undefined ){
              ctx.beginPath();
              ctx.strokeStyle = "#00FF00";
              ctx.arc(vm.addEntityFormGroup.controls.attributes.value.xPos*vm.floorImage.width, vm.addEntityFormGroup.controls.attributes.value.yPos*vm.floorImage.height, 10, 0, 2 * Math.PI);
              ctx.fill();
              ctx.closePath();
              ctx.lineWidth = 2;
              ctx.stroke();


        }


    }

    function FindPosition(oElement) {
        var scrollGap=oElement.parentNode.parentNode.scrollTop
        if (typeof(oElement.offsetParent) != "undefined") {
            for (var posX = 0, posY =
                    0; oElement; oElement = oElement
                .offsetParent) {
                posX += oElement.offsetLeft;
                posY += oElement.offsetTop;
            }
            return [posX, posY-scrollGap];
        } else {
            return [oElement.x, oElement.y];
        }
    }

    function GetCoordinates(e) {

        var PosX = 0;
        var PosY = 0;
        var ImgPos;
        ImgPos = FindPosition(vm.FloorPlanCanvas);
        if (!e) var e = window.event;
        if (e.pageX || e.pageY) {
            PosX = e.pageX;
            PosY = e.pageY;
        } else if (e.clientX || e.clientY) {
            PosX = e.clientX + document.body.scrollLeft +
                document.documentElement.scrollLeft;
            PosY = e.clientY + document.body.scrollTop +
                document.documentElement.scrollTop;
        }
        PosX = PosX - ImgPos[0];
        PosY = PosY - ImgPos[1];
        return [PosX, PosY];
    }

}

