actionButton.customFunction:
let $injector = widgetContext.$scope.$injector;
let customDialog = $injector.get(widgetContext.servicesMap
    .get('customDialog'));
let dialogs = $injector.get(widgetContext.servicesMap.get('dialogs'));

let assetService = $injector.get(widgetContext.servicesMap
    .get('assetService'));
let customerService = $injector.get(widgetContext.servicesMap
    .get('customerService'));
let deviceService = $injector.get(widgetContext.servicesMap
    .get('deviceService'));
let attributeService = $injector.get(widgetContext
    .servicesMap.get('attributeService'));
let entityRelationService = $injector.get(widgetContext
    .servicesMap.get('entityRelationService'));
let entityGroupService = $injector.get(widgetContext
    .servicesMap.get('entityGroupService'));
let userService = $injector.get(widgetContext
    .servicesMap.get('userService'));
openAddEntityDialog();

function openAddEntityDialog() {
    customDialog.customDialog(htmlTemplate,
        AddEntityDialogController).subscribe();
}

function AddEntityDialogController(instance) {
    let vm = instance;
    vm.entity=undefined
    vm.addEntityFormGroup = vm.fb.group({
        entityName: [widgetContext.npNameToNormal(entityName)],
    });
    vm.shouldDeleteFromSystem=false;
    vm.cancel = function() {
        vm.dialogRef.close(null);
    };

    vm.deleteFromSystem=function(value){
        vm.shouldDeleteFromSystem=value
    }
    vm.save = function() {
        console.log(vm.shouldDeleteFromSystem)
        if(vm.shouldDeleteFromSystem){
            deviceService.deleteDevice(entityId.id).subscribe(function(){
                widgetContext.updateAliases();
                widgetContext.parentDashboard.aliasController.updateAliases();

                vm.dialogRef.close(null);
            })
        }
        else{
            // name:vm.entity.customerId.id+"_"+formValues.componentName,
            // _device=
            // vm.entity.customerId=widgetContext.currentUser.tenantId
            var coreName=widgetContext.npNameToNormal(vm.entity.name)
            vm.entity.name=widgetContext.currentUser.tenantId+"_"+coreName
            // vm.entity.customerId={entityType:"TENANT",id:widgetContext.currentUser.tenantId}
            // console.log("vm.entity: ",vm.entity)
            // widgetContext.
            widgetContext.deviceService.saveDevice(vm.entity).subscribe(function(){
                widgetContext.entityRelationService.findByTo(entityId).subscribe(function(relations){
                    widgetContext.rxjs.forkJoin([changeOwner(entityId.id),widgetContext.entityRelationService.deleteRelation(relations[0].from,"Contains",entityId)])
                    .subscribe(function(){

                        widgetContext.updateAliases();
                    widgetContext.parentDashboard.aliasController.updateAliases();
                    vm.dialogRef.close(null);

                    })
                })

            })

        }

    };
    loadData();
    function changeOwner(_deviceId){
        return widgetContext.http.post("/api/owner/TENANT/"+widgetContext.currentUser.tenantId+"/DEVICE/"+_deviceId)
    }
    function loadData(){
        widgetContext.entityService.getEntity(entityId.entityType,entityId.id).subscribe(function(result){
            console.log("delete device: ",result)
            vm.entity=result;

        })
    }

    vm.closeIcon =
        "data:image/png;base64,"

}

