const $injector = widgetContext.$scope.$injector;
const broadcastService = $injector.get(widgetContext.servicesMap.get('broadcastService'));

var status=[]
if(additionalParams["isClear"]==true){
    status.push("CLEARED")
}
if(additionalParams["isActive"]==true){
    status.push("ACTIVE")
}
if(additionalParams["isAck"]==true){
    status.push("ACK")
}
if(additionalParams["isUnack"]==true){
    status.push("UNACK")
}

var severity=[]
if(additionalParams["isCritical"]==true){
    severity.push("CRITICAL")
}
if(additionalParams["isWarning"]==true){
    severity.push("WARNING")
}


broadcastService.broadcast('onAlarmFilter', {"status":status,"severity":severity});

settings.getCounts:
var warning=0
var critical=0
widgetContext.defaultSubscription.alarms.data.forEach(function(e){
    if(e.severity=='CRITICAL'){
        critical=critical+1
    }
    else{
        warning=warning+1
    }
})
return {warning:warning,critical:critical}

