                    widgetContext.rxjs.forkJoin([
                        // widgetContext.entityGroupService.saveEntityGroup({"name":"Customer Users","additionalInfo":{"description":""},"type":"USER","ownerId":cusResult[0].id}),
                        widgetContext.http.get("/api/entityGroups/CUSTOMER/"+cusResult[0].id.id+"/USER"),
